import { Mailer } from 'nodemailer-react'

const transporter = {
    host:"smtp.gmail.com",
    post: 465,
    secure: true,
    auth:{
        user:"sendernovatech@gmail.com",
        pass:"ctkjgrlbosoeafxk",
     }
    };

const PasswordEmail = "sendernovatech@gmail.com"
const ReminderEmail = "ctkjgrlbosoeafxk"

const Email = ({ text }) => ({
    subject: `contact page`,
    body: ({text})
    });

const mailer = Mailer(
    { transporter },
    { Email, PasswordEmail, ReminderEmail }
    )

const sendMail = (text) => {
    mailer.send('Email', { text }, {
        to: 'niev.mauro@gmail.com'
      })
}

module.exports = { sendMail }
